sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    var excelFile = current.variables.file;
    if (!excelFile) {
        gs.error('No Excel file uploaded.');
        return;
    }

    var excelParser = new GlideExcelParser();
    var excelData = excelParser.parse(excelFile, 'Catalog Items'); // Specify sheet name

    // Iterate through the Excel data and create catalog items
    for (var i = 0; i < excelData.length; i++) {
        var catalogItem = new GlideRecord('sc_cat_item');
        catalogItem.initialize();
        catalogItem.name = excelData[i].Name; // Assuming 'name' is a column in the Excel data
		gs.info('The name of catalog item is :'+ catalogItem.name);
        // Set other catalog item fields
        var catalogItemId = catalogItem.insert();
    }

})(current, previous);
